import React from 'react';

class Form extends React.Component{
  constructor(props){
    super(props);
    state = {
      title: ""
    };

    onChange = e => this.setState({ [e.target.name]: e.target.value });

    onSubmit = e => {
      e.preventDefault();
      this.props.addTodo(this.state.title);
      // this.setState({ title: "" });
    };
  }

}

export default Form;
